import React, { useState, useEffect } from 'react';
import Navbar from "../components/Navbar";
import Upload from "./Upload";
import Download from "./DownloadFile";

export default function UserHome() {
  const [isFileUpload, setIsFileUpload] = useState(() => localStorage.getItem("fileUpload") === "true");

  useEffect(() => {
    // Update local state when localStorage changes
    const handleStorageChange = () => {
      setIsFileUpload(localStorage.getItem("fileUpload") === "true");
    };

    window.addEventListener('storage', handleStorageChange);

    // Clean up event listener on component unmount
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const email = localStorage.getItem('userEmail');

  return (
    <div>
      <Navbar />
      {isFileUpload ? <Upload userMail={email} /> : <Download />}
    </div>
  );
}
