import React, { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();

  useEffect(() => {
    // This will run when the component mounts, making sure the correct view is shown based on localStorage
    const fileUpload = localStorage.getItem("fileUpload") === "true";
    if (fileUpload) {
      navigate("/userhome");
    } else {
      navigate("/userhome");
    }
  }, [navigate]);

  const handleFileUpload = () => {
    localStorage.setItem("fileUpload", "true");
    navigate("/userhome"); // Trigger the change
  };

  const handleFileDownload = () => {
    localStorage.setItem("fileUpload", "false");
    navigate("/userhome"); // Trigger the change
  };

  const handleLogOut = () => {
    localStorage.removeItem("authToken");
    localStorage.setItem("fileUpload", "false");
    navigate("/"); // Trigger logout and return to the home page
  };

  const fileUpload = localStorage.getItem("fileUpload") === "true";

  return (
    <nav className="navbar navbar-expand-lg navbar-dark" style={{ backgroundColor: "#e3f2fd" }}>
      <div className="container-fluid">
        <Link className="navbar-brand fs-1 fst-italic text-black" to="/">
          ShareEase
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto mb-2">
            {/* Additional links can be added here */}
          </ul>
          {!localStorage.getItem("authToken") ? (
            <div className="d-flex">
              <Link className="btn bg-white mx-1" to="/login">
                Login
              </Link>
              <Link className="btn bg-white mx-1" to="/signup">
                Sign Up
              </Link>
            </div>
          ) : (
            <div className="d-flex">
              {!fileUpload ? (
                <button className="btn bg-white mx-1" onClick={handleFileUpload}>
                  Upload File
                </button>
              ) : (
                <button className="btn bg-white mx-1" onClick={handleFileDownload}>
                  Your Uploaded Files
                </button>
              )}
              <button className="btn bg-white mx-1" onClick={handleLogOut}>
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}
