import React, { useEffect, useState, useLocation} from 'react';

const FileList = () => {
  const [files, setFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState('');
  const location = useLocation();

  // Destructure the data from the location's state
  const  userMail  = location.state || {userMail: "" }; // Handle case if state is undefined  
  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await fetch('/list-files', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            prefix: `uploads/user-upload/${userMail}/`, // Send the prefix in the request body
          }),
        });

        const data = await response.json();
        setFiles(data);
      } catch (error) {
        console.error('Error fetching files:', error);
      }
    };

    fetchFiles();
  });

  return (
    <div>
      
      <ul>
        {files.map((file) => (
          <li key={file.key}>
            <strong>File:</strong> {file.key} <br />
            <strong>Last Modified:</strong> {new Date(file.lastModified).toLocaleString()} <br />
            <strong>Size:</strong> {file.size} bytes
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FileList;
