import React, { useState, useEffect } from 'react';

const fileCategories = {
  documents: 'Documents',
  images: 'Images',
  videos: 'Videos',
};

export default function FileList() {
  const [selectedCategory, setSelectedCategory] = useState('documents');
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch files based on selected category
  useEffect(() => {
    const fetchFiles = async () => {
      setLoading(true);
      setError(null);

      const email = localStorage.getItem('userEmail');
      const prefix = `uploads/user-upload/${email}/${selectedCategory}/`;

      try {
        const response = await fetch('http://localhost:5000/api/list-files', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email, prefix }),
        });

        const data = await response.json();

        if (data.success) {
          setFiles(data.files);
        } else {
          setError('Failed to fetch files.');
        }
      } catch (err) {
        setError('Error fetching files.');
      } finally {
        setLoading(false);
      }
    };

    fetchFiles();
  }, [selectedCategory]); // Fetch files when selectedCategory changes

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  return (
    <div>
      <h2>Your Uploaded Files</h2>
      <div className="d-flex mb-3">
        {Object.entries(fileCategories).map(([key, label]) => (
          <select
            key={key}
            value={selectedCategory}
            onChange={handleCategoryChange}
            className="form-select me-2"
          >
            {Object.entries(fileCategories).map(([categoryKey, categoryLabel]) => (
              <option key={categoryKey} value={categoryKey}>
                {categoryLabel}
              </option>
            ))}
          </select>
        ))}
      </div>

      {loading && <p>Loading...</p>}
      {error && <p className="text-danger">{error}</p>}

      <ul>
        {files.length > 0 ? (
          files.map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))
        ) : (
          <p>No files found.</p>
        )}
      </ul>
    </div>
  );
}
