import React, { useState, useEffect } from 'react';
import { useAuth } from '../components/AuthContext'; // Import the useAuth hook

const FileList = () => {
    const [fileGroups, setFileGroups] = useState({});
    const { user } = useAuth(); // Use the AuthContext to get the user object

    useEffect(() => {
        const fetchFileGroups = async () => {
            try {
                if (!user || !user.email) {
                    console.error('User email is not available');
                    return;
                }

                const response = await fetch('http://localhost:5000/api/filelist', {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({ userMail: user.email }), // Send userMail for fetching file list
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setFileGroups(data);
            } catch (error) {
                console.error('Error fetching files:', error);
            }
        };

        fetchFileGroups();
    }, [user]);

    return (
        <div>
            {Object.keys(fileGroups).length > 0 ? (
                Object.keys(fileGroups).map(category => (
                    <div key={category}>
                        <h3>{category.charAt(0).toUpperCase() + category.slice(1)}</h3>
                        <select>
                            {fileGroups[category].map(file => (
                                <option key={file.Key} value={file.Key}>
                                    {file.Key}
                                </option>
                            ))}
                        </select>
                    </div>
                ))
            ) : (
                <p>No files available</p>
            )}
        </div>
    );
};

export default FileList;
