import React, { useState, useEffect } from 'react';

const fileCategories = {
  documents: 'Documents',
  images: 'Images',
  videos: 'Videos',
};

export default function FileTypeDropdowns() {
  const [files, setFiles] = useState([]);
  const [allFiles, setAllFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [selectedDocument, setSelectedDocument] = useState('');
  const [selectedImage, setSelectedImage] = useState('');
  const [selectedVideo, setSelectedVideo] = useState('');

  // Fetch all files when the component mounts
  useEffect(() => {
    const fetchFiles = async () => {
      setLoading(true);
      setError(null);

      const email = localStorage.getItem('userEmail');
      console.log('Fetching files for email:', email);

      try {
        const response = await fetch('http://localhost:5000/api/list-files', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email }),
        });

        const data = await response.json();

        if (data.success) {
          setAllFiles(data.files);
          setFiles(data.files); // Set files for initial render
        } else {
          setError('Failed to fetch files.');
        }
      } catch (err) {
        setError('Error fetching files.');
      } finally {
        setLoading(false);
      }
    };

    fetchFiles();
  }, []);

  // Filter files based on selected category
  const filterFiles = (type) => {
    let filteredFiles = [];
    const categoryMapping = {
      documents: 'application',
      images: 'image',
      videos: 'video',
    };

    if (type === 'documents' && selectedDocument) {
      filteredFiles = allFiles.filter(file =>
        file.Key.includes(categoryMapping.documents) &&
        file.Key.endsWith(selectedDocument)
      );
    } else if (type === 'images' && selectedImage) {
      filteredFiles = allFiles.filter(file =>
        file.Key.includes(categoryMapping.images) &&
        file.Key.endsWith(selectedImage)
      );
    } else if (type === 'videos' && selectedVideo) {
      filteredFiles = allFiles.filter(file =>
        file.Key.includes(categoryMapping.videos) &&
        file.Key.endsWith(selectedVideo)
      );
    } else {
      filteredFiles = allFiles.filter(file =>
        file.Key.includes(categoryMapping[type])
      );
    }

    return filteredFiles;
  };

  const handleDocumentChange = (event) => {
    setSelectedDocument(event.target.value);
  };

  const handleImageChange = (event) => {
    setSelectedImage(event.target.value);
  };

  const handleVideoChange = (event) => {
    setSelectedVideo(event.target.value);
  };

  return (
    <div>
      <div className="mb-3 mt-3" style={{ margin: "15px" }}>
        <h5>Documents</h5>
        <select
          value={selectedDocument}
          onChange={handleDocumentChange}
          className="form-select mb-3"
        >
          <option value="">Select a document</option>
          {/* Populate with document types */}
          <option value=".pdf">PDF</option>
          <option value=".docx">DOCX</option>
          <option value=".pptx">PPTX</option>
        </select>
        <ul>
          {filterFiles('documents').map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key.split('/').pop()} {/* Display only the file name */}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))}
        </ul>

        <h5>Images</h5>
        <select
          value={selectedImage}
          onChange={handleImageChange}
          className="form-select mb-3"
        >
          <option value="">Select an image</option>
          {/* Populate with image types */}
          <option value=".jpg">JPG</option>
          <option value=".png">PNG</option>
          <option value=".jpeg">JPEG</option>
        </select>
        <ul>
          {filterFiles('images').map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key.split('/').pop()} {/* Display only the file name */}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))}
        </ul>

        <h5>Videos</h5>
        <select
          value={selectedVideo}
          onChange={handleVideoChange}
          className="form-select mb-3"
        >
          <option value="">Select a video</option>
          {/* Populate with video types */}
          <option value=".mp4">MP4</option>
          <option value=".avi">AVI</option>
          <option value=".mov">MOV</option>
        </select>
        <ul>
          {filterFiles('videos').map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key.split('/').pop()} {/* Display only the file name */}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))}
        </ul>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p className="text-danger">{error}</p>}
    </div>
  );
}
