import React, { useState, useEffect } from 'react';

const fileCategories = {
  document: 'Document',
  image: 'Image',
  video: 'Video',
};

export default function FileTypeDropdowns() {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [files, setFiles] = useState([]);
  const [allFiles, setAllFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch all files when the component mounts
  useEffect(() => {
    const fetchFiles = async () => {
      setLoading(true);
      setError(null);

      const email = localStorage.getItem('userEmail');
      console.log('Fetching files for email:', email);

      try {
        const response = await fetch('http://localhost:5000/api/list-files', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email }),
        });

        const data = await response.json();

        if (data.success) {
          setAllFiles(data.files);
          setFiles(data.files); // Set files for initial render
        } else {
          setError('Failed to fetch files.');
        }
      } catch (err) {
        setError('Error fetching files.');
      } finally {
        setLoading(false);
      }
    };

    fetchFiles();
  }, []);

  // Filter files based on selected category
  useEffect(() => {
    if (selectedCategory) {
      const filteredFiles = allFiles.filter(file => file.type === fileCategories[selectedCategory]);
      setFiles(filteredFiles);
    } else {
      setFiles(allFiles);
    }
  }, [selectedCategory, allFiles]);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  return (
    <div>
      <div className="mb-3 mt-3" style={{ margin: "15px" }}>
        <select
          value={selectedCategory}
          onChange={handleCategoryChange}
          className="form-select"
        >
          <option value="">Select a category</option>
          {Object.keys(fileCategories).map(key => (
            <option key={key} value={key}>{fileCategories[key]}</option>
          ))}
        </select>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p className="text-danger">{error}</p>}
      <ul>
        {files.length > 0 ? (
          files.map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key.split('/').pop()}  {/* Display only the file name */}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))
        ) : (
          <p>No files found.</p>
        )}
      </ul>
    </div>
  );
}
