import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'; // Ensure this import is present

const FileList = () => {
  const [files, setFiles] = useState([]);
  const location = useLocation();

  // Destructure userMail from location state
  const { userMail } = location.state || { userMail: "" }; // Default to an empty string if undefined

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await fetch('/list-files', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            prefix: `uploads/user-upload/${userMail}/`, // Send the prefix in the request body
          }),
        });

        const data = await response.json();
        setFiles(data);
      } catch (error) {
        console.error('Error fetching files:', error);
      }
    };

    // Only fetch files if userMail is present
    if (userMail) {
      fetchFiles();
    }
  }, [userMail]); // Dependency array includes userMail to rerun the effect when it changes

  return (
    <div className='text-white'>
      <h2>Files for {userMail}</h2>
      <ul>
        {files.map((file) => (
          <li key={file.key}>
            <strong>File:</strong> {file.key} <br />
            <strong>Last Modified:</strong> {new Date(file.lastModified).toLocaleString()} <br />
            <strong>Size:</strong> {file.size} bytes
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FileList;
