import React, { useState } from 'react';

function FileUpload() {
  const [file, setFile] = useState(null);
  const [uploadedFileName, setUploadedFileName] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setUploadedFileName(e.target.files[0].name);
  };

  const uploadFile = async () => {
    if (!file) return;
    
    try {
      // Step 1: Get the presigned URL from the backend
      const response = await fetch('http://localhost:5000/api/generate-url', {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
      });

      const data = await response.json();
      const { url, filename } = data;

      // Step 2: Upload the file directly to S3 using the presigned URL
      const uploadResponse = await fetch(url, {
        method: "PUT",
        body: file,
        headers: {
          'Content-Type': file.type,
        },
      });

      if (uploadResponse.ok) {
        console.log('File uploaded successfully:', filename);
        alert(`File uploaded: ${filename}`);
      } else {
        throw new Error('Upload failed');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
      alert("Error occurred!!");
    }
  };

  return (
    <div>
      <section className=" py-3 py-md-5">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-12 col-sm-10 col-md-8 col-lg-6 col-xl-5 col-xxl-4">
              <div className="card border border-light-subtle rounded-3 shadow-sm">
                <div className="card-body p-3 p-md-4 p-xl-5">

                  <h2 className="fs-6 fw-normal text-center  mb-4">Select File for Uploading</h2>
                  <input className="form-control" onChange={handleFileChange} type="file" id="formFileMultiple" multiple />
                  <div className="col-12">
                    <div className="d-grid my-3">
                      <button className="btn btn-primary btn-lg text-black" style={{ "backgroundColor": "#e3f2fd" }} type="submit" onClick={uploadFile}>Upload</button>
                    </div>
                  </div>
                  
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
    // <div className='container'>
    //   <h2>Upload File to S3</h2>
    //   <input type="file" onChange={handleFileChange} />
    //   <button onClick={uploadFile}>Upload</button>
    // </div>
  );
}

export default FileUpload;
