import React, { useState, useEffect } from 'react';

export default function MyUploads() {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch files when the component mounts
  useEffect(() => {
    const fetchFiles = async () => {
      setLoading(true);
      setError(null);

      const email = localStorage.getItem('userEmail');
      console.log('Fetching files for email:', email); // Print the email before posting

      try {
        const response = await fetch('http://localhost:5000/api/list-files', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email }),
        });

        const data = await response.json();

        if (data.success) {
          setFiles(data.files);
        } else {
          setError('Failed to fetch files.');
        }
      } catch (err) {
        setError('Error fetching files.');
      } finally {
        setLoading(false);
      }
    };

    fetchFiles();
  }, []); // Empty dependency array means this effect runs once when the component mounts

  return (
    <div>
      {loading && <p>Loading...</p>}
      {error && <p className="text-danger">{error}</p>}

      <ul>
        {files.length > 0 ? (
          files.map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))
        ) : (
          <p>No files found.</p>
        )}
      </ul>
    </div>
  );
}
