import React, { useState, useEffect } from 'react';

export default function FileTypeDropdowns() {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedDocument, setSelectedDocument] = useState('');
  const [selectedImage, setSelectedImage] = useState('');
  const [selectedVideo, setSelectedVideo] = useState('');

  // Fetch all files when the component mounts
  useEffect(() => {
    const fetchFiles = async () => {
      setLoading(true);
      setError(null);

      const email = localStorage.getItem('userEmail');
      console.log('Fetching files for email:', email);

      try {
        const response = await fetch('http://localhost:5000/api/list-files', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email }),
        });

        const data = await response.json();

        if (data.success) {
          setFiles(data.files);
        } else {
          setError('Failed to fetch files.');
        }
      } catch (err) {
        setError('Error fetching files.');
      } finally {
        setLoading(false);
      }
    };

    fetchFiles();
  }, []);

  // Filter files based on selected category
  const filterFiles = (category) => {
    let filteredFiles = [];

    switch (category) {
      case 'documents':
        filteredFiles = files.filter(file => file.ContentType.startsWith('application'));
        break;
      case 'images':
        filteredFiles = files.filter(file => file.ContentType.startsWith('image'));
        break;
      case 'videos':
        filteredFiles = files.filter(file => file.ContentType.startsWith('video'));
        break;
      default:
        filteredFiles = files;
    }

    return filteredFiles;
  };

  const handleDocumentChange = (event) => {
    setSelectedDocument(event.target.value);
  };

  const handleImageChange = (event) => {
    setSelectedImage(event.target.value);
  };

  const handleVideoChange = (event) => {
    setSelectedVideo(event.target.value);
  };

  return (
    <div>
      <div className="mb-3 mt-3" style={{ margin: "15px" }}>
        <h5>Documents</h5>
        <select
          value={selectedDocument}
          onChange={handleDocumentChange}
          className="form-select mb-3"
        >
          <option value="">Select a document</option>
          {/* Add options dynamically based on fetched files */}
          {filterFiles('documents').map((file, index) => (
            <option key={index} value={file.Key}>{file.Key.split('/').pop()}</option>
          ))}
        </select>
        <ul>
          {filterFiles('documents').filter(file => file.Key === selectedDocument).map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key.split('/').pop()} {/* Display only the file name */}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))}
        </ul>

        <h5>Images</h5>
        <select
          value={selectedImage}
          onChange={handleImageChange}
          className="form-select mb-3"
        >
          <option value="">Select an image</option>
          {/* Add options dynamically based on fetched files */}
          {filterFiles('images').map((file, index) => (
            <option key={index} value={file.Key}>{file.Key.split('/').pop()}</option>
          ))}
        </select>
        <ul>
          {filterFiles('images').filter(file => file.Key === selectedImage).map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key.split('/').pop()} {/* Display only the file name */}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))}
        </ul>

        <h5>Videos</h5>
        <select
          value={selectedVideo}
          onChange={handleVideoChange}
          className="form-select mb-3"
        >
          <option value="">Select a video</option>
          {/* Add options dynamically based on fetched files */}
          {filterFiles('videos').map((file, index) => (
            <option key={index} value={file.Key}>{file.Key.split('/').pop()}</option>
          ))}
        </select>
        <ul>
          {filterFiles('videos').filter(file => file.Key === selectedVideo).map((file, index) => (
            <li key={index}>
              <a href={`https://your-bucket-name.s3.amazonaws.com/${file.Key}`} target="_blank" rel="noopener noreferrer">
                {file.Key.split('/').pop()} {/* Display only the file name */}
              </a>
              <span> ({file.Size} bytes)</span>
            </li>
          ))}
        </ul>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p className="text-danger">{error}</p>}
    </div>
  );
}
