import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  
  const handleFileUpload = () => {
    localStorage.setItem("fileUpload", "false");
    navigate("/");
  };

  const handleFileDownload = () => {
    localStorage.setItem("fileUpload", "true");
    navigate("/");
  };

  const handleLogOut = () => {
    localStorage.removeItem("authToken");
    navigate("/");
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark" style={{ backgroundColor: "#e3f2fd" }}>
      <div className="container-fluid">
        <Link className="navbar-brand fs-1 fst-italic text-black" to="/">
          ShareEase
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto mb-2">
            {/* You might want to add more links here */}
          </ul>
          {!localStorage.getItem("authToken") ? (
            <div className="d-flex">
              <Link className="btn bg-white mx-1" to="/login">
                Login
              </Link>
              <Link className="btn bg-white mx-1" to="/signup">
                Sign Up
              </Link>
            </div>
          ) : (
            <div className="d-flex">
              {isFileUpload ? <Link
                className="btn bg-white mx-1"
                onClick={handleFileDownload}
              >
                My Uploaded Files
              </Link> : 
              <Link
                className="btn bg-white mx-1"
                onClick={handleFileUpload}
              >
                Upload Files
              </Link>}
              
              <button className="btn bg-white mx-1" onClick={handleLogOut}>
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}
